    console.log('fetching address!!!')
    let address = '1AJbsFZ64EpEfS5UAjAfcUG8pH8Jn3rn1F'
    const res = await axios.get(`https://blockchain.info/rawaddr/${address}`, { crossdomain: true })
    // const res = await axios.get('/api/fetch_address')
    console.log('christy results', res)
	// dispatch({ type: FETCH_USER, payload: res.data });
};
