import React, { PureComponent } from 'react'
import injectSheet from 'react-jss'
import compose from 'recompose/compose'
import pure from 'recompose/pure'
import SearchIcon from './search_icon.png'
import BlockchainWordmark from './Blockchain-Wordmark.png'
import BlockchainLogo from './Blockchain-Logo.png'

const styleSheet = theme => ({
    container: {
        margin: 0,
        backgroundColor: '#ffffff'
    },
    header: {
        height: '50px',
        padding: '20px',
    },
    searchBar: {
        backgroundColor: '#004a7c',
        display: 'flex',
        justifyContent: 'center', 
        alignItems: 'center',
        height: '90px',
        '& input': {
            width: '80%',
            height: '30px',
            fontSize: '14px',
        },
    },
    searchButton: {
        height: '50px',
        background: 'none',
        border: 'none'
    },
    logo: {
        width: '40%',
        maxWidth: '300px'
    },
    results: {
        margin: '20px 70px',
        '& table': {
            marginBottom: '20px',
        },
        '& th': {
            textAlign: 'left'
        },
        '& td:first-child': {
            width: '150px'
        }
    },
    transactions: {
        margin: '20px 70px',
    },
    hash: {
        fontSize: '11px'
    }
})

const style = injectSheet(styleSheet)

class SearchPage extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            address: '1LuckyR1fFHEsXYyx5QK4UFzv3PEAepPMK'
        }
    }

    updateAddress = (value) => {
        this.setState({ address: value })
    }

    renderInputs = (hash, inputs) => {
        if(inputs) {
            <table>
                {inputs.map((input) => {
                     <tr>
                        <td>{hash}</td>
                        <td>{input.prev_out.addr}</td>
                        <td>{input.prev_out.value}</td>
                    </tr>
                })}
            </table>
        }
    }

    renderOuts = (hash, outs) => {
        if(outs) {
            <table>
                {outs.map((out) => {
                    <tr>
                        <td>{hash}</td>
                        <td>{`${out.addr} (${out.addr_tag})`}</td>
                        <td>{out.value}</td>
                    </tr>
                })}
            </table>
        }
    }

    renderTransactions = (transaction) => {
        const { classes } = this.props;
        let inputs, outputs

        if(transaction.inputs) {
            inputs = this.renderInputs(transaction.hash, transaction.inputs)
        }

        if(transaction.out) {
            outputs = this.renderOuts(transaction.hash, transaction.out)
        }
        return(<div>
            <h3>UNDER ONE HASH: {transaction.hash}</h3>
            {inputs}
            {outputs}
        </div>)
    }

    render() {
        const { searchAddress, classes, search } = this.props;
        const { address } = this.state;

        console.log('final result from reducers', search)
        //1LuckyR1fFHEsXYyx5QK4UFzv3PEAepPMK

        return(
            <div className={classes.container}>
                <div className={classes.header}>
                    <img className={classes.logo} src={BlockchainLogo} />
                </div>
                <div className={classes.searchBar}>
                    <input type="text" placeholder="Lookup bitcoin addresses..." onChange={({target: {value}})=>this.updateAddress(value)}/>
                    <button className={classes.searchButton} onClick={() => searchAddress(address)}><img src={SearchIcon} /></button>
                </div>
                {search.results && <div className={classes.results}>
                    <table>
                        <tr>
                            <th>Summary</th>
                            <th></th>
                        </tr>
                        <tr>
                            <td>Address</td>
                            <td>{search.results.address}</td>
                        </tr>
                        <tr>
                            <td>Hash 160</td>
                            <td>{search.results.hash160}</td>
                        </tr>
                    </table>
                    <table>
                        <tr>
                            <th>Transactions</th>
                            <th></th>
                        </tr>
                        <tr>
                            <td>No. Transactions</td>
                            <td>{search.results.n_tx}</td>
                        </tr>
                        <tr>
                            <td>Total Received</td>
                            <td>{search.results.total_received}</td>
                        </tr>
                        <tr>
                            <td>Final Balance</td>
                            <td>{search.results.final_balance}</td>
                        </tr>
                    </table>
                </div>}
                {search.results && <div className={classes.transactions}>
                    {this.renderTransactions(search.results)}
                    <h3>Transactions</h3>
                    <div className={classes.transactionData}>
                        {search.results.txs.map((transaction) => this.renderTransactions(transaction))}
                    </div>
                </div>}
            </div>
        )
    }
}

SearchPage.propTypes = {
}

SearchPage.defaultProps = {
}

export default compose(
    style,
    pure
)(SearchPage)
