import React, { PureComponent } from 'react'
import injectSheet from 'react-jss'
import compose from 'recompose/compose'
import pure from 'recompose/pure'
import SearchIcon from './search_icon.png'
import BlockchainWordmark from './Blockchain-Wordmark.png'
import BlockchainLogo from './Blockchain-Logo.png'

const styleSheet = theme => ({
    container: {
        margin: 0,
        backgroundColor: '#ffffff'
    },
    header: {
        backgroundColor: '#004a7c',
        height: '50px',
        padding: '10px',
    },
    searchBar: {
        display: 'flex',
        justifyContent: 'center', 
        alignItems: 'center',
        height: '100%',
        '& input': {
            width: '50%',
            height: '30px'
        },
    },
    searchButton: {
        height: '50px',
        background: 'none',
        border: 'none'
    },
    logo: {
        width: '40%',
        maxWidth: '300px'
    }
})

const style = injectSheet(styleSheet)

class SearchPage extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            address: null
        }
    }

    updateAddress = (value) => {
        this.setState({ address: value })
    }

    render() {
        const { searchAddress, classes, search } = this.props;
        const { address } = this.state;

        console.log('final result from reducers', search)

        return(
            <div className={classes.container}>
                <div className={classes.header}>
                    <img className={classes.logo} src={BlockchainLogo} />
                    {/* <div className={classes.searchBar}>
                        <input type="text" placeholder="Lookup bitcoin addresses..." onChange={({target: {value}})=>this.updateAddress(value)}/>
                        <button className={classes.searchButton} onClick={() => searchAddress(address)}><img src={SearchIcon} /></button>
                    </div> */}
                </div>
                {search.results && <div className={classes.results}>
                    <h3>Summary</h3>
                    <div className={classes.address}>{search.results.address}</div>
                    <div className={classes.hash}>{search.results.hash160}</div>
                    <h3>Transactions</h3>
                    <div className={classes.transactions}>{search.results.n_tx}</div>
                    <div className={classes.balance}>{search.results.final_balance}</div>
                </div>}
            </div>
        )
    }
}

SearchPage.propTypes = {
}

SearchPage.defaultProps = {
}

export default compose(
    style,
    pure
)(SearchPage)
