import React, { PureComponent } from 'react'
import injectSheet from 'react-jss'
import compose from 'recompose/compose'
import pure from 'recompose/pure'

const styleSheet = theme => ({
    container: {
        margin: 0,
        backgroundColor: '#ffffff'
    },
    header: {
        backgroundColor: '#004a7c',
        height: '250px'
    },
    searchBar: {
        display: 'flex',
        justifyContent: 'center', 
        alignItems: 'center',
        height: '100%',
        '& input': {
            width: '50%',
            height: '30px'
        }
    }
})

const style = injectSheet(styleSheet)

class AddressPage extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            address: null
        }
    }

    updateAddress = (value) => {
        this.setState({ address: value })
    }

    render() {
        const { searchAddress, classes } = this.props;
        const { address } = this.state;

        return(
            <div className={classes.container}>
                <div className={classes.header}>
                    <div className={classes.searchBar}>
                        <input type="text" placeholder="Search.." onChange={({target: {value}})=>this.updateAddress(value)}/>
                    </div>
                </div>
                <button onClick={() => searchAddress(address)}>Click me</button>
            </div>
        )
    }
}

AddressPage.propTypes = {
}

AddressPage.defaultProps = {
}

export default compose(
    style,
    pure
)(AddressPage)
